import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'customers-angular';
}
<div class="toolbar d-flex align-items-center">
  <nav class="d-flex align-items-center navbar fixed-top navbar-expand-md navbar-light white double-nav scrolling-navbar">
     <div class="col-sm-6 d-flex align-items-center">
       <h4><i class='fas fa-home' routerLink="/customers"></i></h4>
     </div>
     <div class="col-sm-6">
       <span class="user"><b><i class='fas fa-user'></i> Customers</b></span>
     </div>
  </nav>
</div>
<div>
  <router-outlet></router-outlet>
</div>
